let droit\n        - medtheCs1CircoChevilleDt,nolabel,plusg={D :},plus={cm},class={text-right},tabindex=8 		#16042 Circonférence cheville droite\n\r\n  row3:  \r\n    col1:                            \r\n      size: col-12\r\n      bloc:  \r\n        - medtheCs1ExamenGen,rows=3,tabindex=9     		#16044 Examen général', '', '', '', '$(document).ready(function() {\r\n\r\n  //calcul IMC\r\n  if ($(\'#id_imc_id\').length > 0) {\r\n\r\n    $(\"#nouvelleCs\").on(\"keyup\", \"#id_poids_id , #id_taillePatient_id\", function() {\r\n      poids = $(\'#id_poids_id\').val();\r\n      taille = $(\'#id_taillePatient_id\').val();\r\n      imc = imcCalc(poids, taille);\r\n      $(\'#id_imc_id\').val(imc);\r\n    });\r\n  }\r\n\r\n  //ajutement auto des textarea en hauteur\r\n  autosize($(\'#formName_medtheFormCsTherm1 textarea\')); \r\n  \r\n});'),
